import Image from './Image';
import ImageLarger from './ImagesForLarge';
import classes from './SearchMediaLayout.module.css';
import {useEffect, useState} from "react";
import Card from "../UI/Card";
import {galleryActions} from "../../store/images-slice";
import {useDispatch} from "react-redux";

const SearchMediaLayout = ({searchTerm}) => {
    const dispatch = useDispatch();
    const [loadedImages, setLoadedImages] = useState([])
    const [selectedImage, setSelectedImage] = useState('')
    const fetchImages = () => {
        (async () => {
            const baseUrl = 'https://api.unsplash.com/search/photos?client_id=8tIIkbGFrxgSQQ9mOkHiXhbenY2VNescsdy2HYcljVs&per_page=15&query='
            const response = await fetch(baseUrl+`${searchTerm}`);
            const responseJson = await response.json();
            const responseData = responseJson.results;
            setLoadedImages(responseData.map(({id, urls: {thumb, full}, description, height, width, created_at}) => {
                return {
                    id: id,
                    url: thumb,
                    url_full: full,
                    name: id,
                    description: description,
                    height: height,
                    width: width,
                    date: created_at,
                    isChecked: false
                }
            }))
        })().catch(err => {
            
            console.error(err);
        });
    }


    useEffect(() => {
        setSelectedImage('')
        if (searchTerm) {
            fetchImages()
        }
    }, [searchTerm])

    return (
		<div className={classes.conatiner}>
        <section className={classes.media}>
            <div className={classes.row}>
                {selectedImage &&
                <ImageLarger name={selectedImage.name}   height={selectedImage.height}  width={selectedImage.width} id={selectedImage.id} url={selectedImage.url_full} description={selectedImage.description} />
                }
                {!selectedImage && (searchTerm ? loadedImages.length === 0
                        ? (<h4>Uh-oh! Nothing to show.</h4>)
                        : loadedImages.map(img => (
                            <Card key={img.id}>
                                <Image
                                    showSelectButton={false}
                                    id={img.id}
                                    name={img.name}
                                    description={img.description}
                                    height={img.height}
                                    width={img.width}
                                    url={img.url}
                                    isChecked={img.isChecked}
                                    onClick={() => {
                                        setSelectedImage(img)
                                        dispatch(galleryActions.addToDummyGallery(img))
                                        
                                    }}
                                />
                              
                            </Card>))
                    : (<h4>Make a search to get started!!!</h4>))
                }
            </div>
        </section>
		</div>
    )
}


export default SearchMediaLayout;
