import { useDispatch } from "react-redux";
import { galleryActions } from "../../store/images-slice";
import classes from "./Imagelarger.module.css";

function ImageLarger({
  
  url,
  description,
  name,
  showSelectButton,
  onClick,
  })
 {
  const dispatch = useDispatch();
  const imageSubmitHandler = () => {
    dispatch(galleryActions.addToGallery(url))
  };
  return (
    <>
      <img
        onClick={(a) => onClick && onClick(a)}
        src={url}
        className={classes.img}
        alt={description}
      ></img>
      {showSelectButton && (
        <input
          type="Submit"
          onClick={imageSubmitHandler}
        ></input>
      )}
      <p className={classes.caption}>{name}</p>
    </>
  );
}

export default  ImageLarger;
