import { useDispatch, useSelector} from "react-redux";
import { galleryActions } from "../../store/images-slice";
import classes from "./ImagesForLarge.module.css";

function ImageLarger({
 id, name,description,url,height,width
})
 {
  const dispatch = useDispatch();
  const selectedImage = useSelector(state => state.gallery.dummyImages);
  const AllImage = useSelector(state => state.gallery.images);
  console.log(selectedImage)
  console.log(AllImage)
  const imageSubmitHandler = () => {
    dispatch(galleryActions.addToGallery(selected));
  };
 const selected = selectedImage.map(img =>(
           
            id=img.id,
            name=img.name,
            description=img.description,
            height=img.height,
            width=img.width,
            url=img.url
                
 ) )
  return (
    <>
      <img
        src={url}
        className={classes.img}
        alt={description}
      ></img>
      
        <button
          className={classes.btn}
          onClick={imageSubmitHandler}
        >Submit</button>
      
      <h1 className={classes.caption}>{name}</h1>
      <br></br>
      <h2 className={classes.caption}>{height}*{width}</h2>
    
    </>
  );
}

export default  ImageLarger;
