import { useState } from "react";
import { useDispatch, useSelector} from "react-redux";
import { galleryActions } from "../../store/images-slice";
import classes from "./ImagesForLarge.module.css";

function ImageLarger({
  name,description,url,height,width
})
 {
   const [selectedImages, setSelectedImages]= useState('')
  const dispatch = useDispatch();
  const selectedImage = useSelector(state => state.gallery.dummyImages);
  const AllImage = useSelector(state => state.gallery.images);
  setSelectedImages(selectedImage.map(({id, url, description, height, width, created_at}) => {
    return {
        id: id,
        url: thumb,
        url_full: full,
        name: id,
        description: description,
        height: height,
        width: width,
        date: created_at,
        isChecked: false
    }
}))
  console.log(selectedImage)
  console.log(AllImage)
  const imageSubmitHandler = (e) => {
    e.preventDefault()
    dispatch(galleryActions.addToGallery(selectedImage));
  };
  return (
    <>
      <img
        src={url}
        className={classes.img}
        alt={description}
      ></img>
      
        <button
          className={classes.btn}
          onClick={imageSubmitHandler}
        >Submit</button>
      
      <h1 className={classes.caption}>{name}</h1>
      <br></br>
      <h2 className={classes.caption}>{height}*{width}</h2>
    
    </>
  );
}

export default  ImageLarger;
