import Image from './Image';
import ImageLarge from './ImageLarge';
import PropTypes from 'prop-types'
import classes from './SearchMediaLayout.module.css';
import {useEffect, useState} from "react";
import Card from "../UI/Card";
const SearchMediaLayout = ({searchTerm}) => {
    const [loadedImages, setLoadedImages] = useState([])
    const [selectedImage, setSelectedImage] = useState('')
    const fetchImages = () => {
        (async () => {
            const url = `https://api.unsplash.com/search/photos?client_id=8tIIkbGFrxgSQQ9mOkHiXhbenY2VNescsdy2HYcljVs&per_page=15&query=${searchTerm}`
            const response = await fetch(url);
            const responseJson = await response.json();
            const responseData = responseJson.results;
            setLoadedImages(responseData.map(({id, urls: {thumb, regular}, description, height, width, created_at}) => {
                return {
                    id: id,
                    url: thumb,
                    url_regular: regular,
                    name: `img${id}`,
                    description: description,
                    height: height,
                    width: width,
                    date: created_at,
                    isChecked: false
                }
            }))
        })().catch(err => {
            
            console.error(err);
        });
    }


    useEffect(() => {
        setSelectedImage('')
        if (searchTerm) {
            fetchImages()
        }
    }, [searchTerm])

    return (
		<div className={classes.conatiner}>
        <section className={classes.media}>
            <div className={classes.row}>
                {!selectedImage && (searchTerm ? loadedImages.length === 0
                        ? (<h4>Uh-oh! Nothing to show.</h4>)
                        : loadedImages.map(img => (
                            <Card key={img.id}>
                                <Image
                                    showSelectButton={false}
                                    id={img.id}
                                    name={img.name}
                                    description={img.description}
                                    height={img.height}
                                    width={img.width}
                                    url={img.url}
                                    isChecked={img.isChecked}
                                    onClick={() => {
                                        setSelectedImage(img)
                                    }
                                }
                                />
                            </Card>))
                    : (<h4>Make a search to get started!!!</h4>))
                }
            </div>
        </section>
		</div>
    )
}

SearchMediaLayout.propTypes = {
    searchTerm: PropTypes.string.isRequired,
}

export default SearchMediaLayout;
